import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <User user={user} />
        <UsersList list={persons} />
      </header>
    </div>
  );
};

const Avatar = props => <img src={props.user.img} alt={props.user.name} />;
const UserName = props => <p>{props.user.name}</p>;

const User = props => {
  return (
    <div>
      <Avatar user= {props.user} />
      <UserName user = {props.user} />
    </div>
  )
};

const UsersList = props => {
  const userlist =props.list.map((user,i) => <User user={user} key={i} />);
  return (
    <div className="user-list">
      {userList}
    </div>
  )
};

const user = {
  name: 'Alexander Henao',
  img: logo
};


const persons = [
  {
    name: 'Carlos',
    avatar: 'https://picsum.photos/100/200'
  },
  {
    name: 'Pedro',
    avatar: 'https://picsum.photos/100/200'
  },
  {
    name: 'Sergio',
    avatar: 'https://picsum.photos/100/200'
  },
  {
    name: 'David',
    avatar: 'https://picsum.photos/100/200'
  }
]
export default App;
